SELECT sub1.*, stu.`name` AS stuname FROM ( SELECT sub.*, tea.`name` AS teaname, tf.contentTitle AS tfcontentTitle, tf.contentLocation AS tfcontentLocation, tf.contentCreateTime AS tfcontentCreateTime, tf.proposalTitle AS tfproposalTitle, tf.proposalLocation AS tfproposalLocation, tf.proposalCreateTime AS tfproposalCreateTime, tf.proposalScore AS tfproposalScore, tf.demandTitle AS tfdemandTitle, tf.demandLocation AS tfdemandLocation, tf.demandCreateTime AS tfdemandCreateTime, tf.demandScore AS tfdemandScore, tf.diaryTitle AS tfdiaryTitle, tf.diaryLocation AS tfdiaryLocation, tf.diaryCreateTime AS tfdiaryCreateTime, tf.diaryScore AS tfdiaryScore, tf.codeTitle AS tfcodeTitle, tf.codeLocation AS tfcodeLocation, tf.codeCreateTime AS tfcodeCreateTime, tf.codeScore AS tfcodeScore, tf.summaryTitle AS tfsummaryTitle, tf.summaryLocation AS tfsummaryLocation, tf.summaryCreateTime AS tfsummaryCreateTime, tf.summaryScore AS tfsummaryScore, tf.score AS tfscore, tf.`comment` AS tfcomment FROM t_subject sub, t_teacher tea, t_file tf WHERE sub.teacherId = tea.id AND tf.id=sub.id
AND sub.teacherId= #{subject.teacherId}
AND sub.studentId= #{subject.studentId}
AND sub.`name` LIKE CONCAT('%',#{subject.name}, '%')
AND sub.`status` = #{subject.status}
AND sub.createDate BETWEEN #{createDateScope[0]} AND #{createDateScope[1]}
AND sub.openDate BETWEEN #{openDateScope[0]} AND #{openDateScope[1]}
ORDER BY sub.id ) AS sub1 LEFT JOIN t_student stu ON sub1.studentId = stu.id ORDER BY sub1.id ASC

SELECT sub1.*, stu.`name` AS stuname, stu.phone AS stuphone, stu.dept AS studept, stu.major AS stumajor FROM ( SELECT sub.*, tea.`name` AS teaname, tf.contentTitle AS tfcontentTitle, tf.contentLocation AS tfcontentLocation, tf.contentCreateTime AS tfcontentCreateTime, tf.proposalTitle AS tfproposalTitle, tf.proposalLocation AS tfproposalLocation, tf.proposalCreateTime AS tfproposalCreateTime, tf.proposalScore AS tfproposalScore, tf.demandTitle AS tfdemandTitle, tf.demandLocation AS tfdemandLocation, tf.demandCreateTime AS tfdemandCreateTime, tf.demandScore AS tfdemandScore, tf.diaryTitle AS tfdiaryTitle, tf.diaryLocation AS tfdiaryLocation, tf.diaryCreateTime AS tfdiaryCreateTime, tf.diaryScore AS tfdiaryScore, tf.codeTitle AS tfcodeTitle, tf.codeLocation AS tfcodeLocation, tf.codeCreateTime AS tfcodeCreateTime, tf.codeScore AS tfcodeScore, tf.summaryTitle AS tfsummaryTitle, tf.summaryLocation AS tfsummaryLocation, tf.summaryCreateTime AS tfsummaryCreateTime, tf.summaryScore AS tfsummaryScore, tf.score AS tfscore, tf.`comment` AS tfcomment FROM t_subject sub, t_teacher tea, t_file tf WHERE sub.teacherId = tea.id AND tf.id=sub.id AND sub.teacherId= #{subject.teacherId}
AND sub.studentId= #{subject.studentId}
AND sub.`name` LIKE CONCAT('%',#{subject.name}, '%')
AND sub.`status` = #{subject.status}
AND(sub.`status`!='已结题')
AND sub.createDate BETWEEN #{createDateScope[0]} AND #{createDateScope[1]}
AND sub.openDate BETWEEN #{openDateScope[0]} AND #{openDateScope[1]}
ORDER BY sub.id ) AS sub1 LEFT JOIN t_student stu ON sub1.studentId = stu.id ORDER BY sub1.id ASC

SELECT sub1.*, stu.`name` AS stuname FROM ( SELECT sub.*, tea.`name` AS teaname, tea.dept AS teadept, tea.major AS teamajor, tea.phone AS teaphone, tf.contentTitle AS tfcontentTitle, tf.contentLocation AS tfcontentLocation, tf.contentCreateTime AS tfcontentCreateTime, tf.proposalTitle AS tfproposalTitle, tf.proposalLocation AS tfproposalLocation, tf.proposalCreateTime AS tfproposalCreateTime, tf.proposalScore AS tfproposalScore, tf.demandTitle AS tfdemandTitle, tf.demandLocation AS tfdemandLocation, tf.demandCreateTime AS tfdemandCreateTime, tf.demandScore AS tfdemandScore, tf.diaryTitle AS tfdiaryTitle, tf.diaryLocation AS tfdiaryLocation, tf.diaryCreateTime AS tfdiaryCreateTime, tf.diaryScore AS tfdiaryScore, tf.codeTitle AS tfcodeTitle, tf.codeLocation AS tfcodeLocation, tf.codeCreateTime AS tfcodeCreateTime, tf.codeScore AS tfcodeScore, tf.summaryTitle AS tfsummaryTitle, tf.summaryLocation AS tfsummaryLocation, tf.summaryCreateTime AS tfsummaryCreateTime, tf.summaryScore AS tfsummaryScore, tf.score AS tfscore, tf.`comment` AS tfcomment FROM t_subject sub, t_teacher tea, t_file tf WHERE sub.teacherId = tea.id AND tf.id=sub.id AND sub.studentId = #{subject.studentId}
AND sub.teacherId= #{subject.teacherId}
AND sub.`name` LIKE CONCAT('%',#{subject.name}, '%')
AND sub.`status` = #{subject.status}
AND(sub.`status`!='已结题')
AND sub.createDate BETWEEN #{createDateScope[0]} AND #{createDateScope[1]}
